    var __freezeStencil = function(journey) {
        UserAgentUtils.freeze();
    };

    /**
     * __normalizeComparisonValue
     * 
     * @access  private
     * @param   mixed value
     * @return  mixed
     */
    var __normalizeComparisonValue = function(value) {

        // Nothing to normalize
        if (value.constructor === Number) {
            return value;
        }

        // Nothing to normalize
        if (value.constructor === Array) {
            return value;
        }

        // Nothing to normalize
        if (value.constructor === Boolean) {
            return value;
        }

        // Seconds check
        var matches = value.match(/^([0-9\.]+) second[s]?$/i);
        if (matches !== null) {
            var seconds = matches.pop();
            value = seconds.toFloat();
            return value;
        }

        // Minutes check
        matches = value.match(/^([0-9\.]+) minute[s]?$/i);
        if (matches !== null) {
            var minutes = matches.pop();
            value = minutes.toFloat() * 60;
            return value;
        }

        // Hours check
        matches = value.match(/^([0-9\.]+) hour[s]?$/i);
        if (matches !== null) {
            var hours = matches.pop();
            value = hours.toFloat() * 60 * 60;
            return value;
        }

        // Days check
        matches = value.match(/^([0-9\.]+) day[s]?$/i);
        if (matches !== null) {
            var days = matches.pop();
            value = days.toFloat() * 24 * 60 * 60;
            return value;
        }

        // Weeks check
        matches = value.match(/^([0-9\.]+) week[s]?$/i);
        if (matches !== null) {
            var weeks = matches.pop();
            value = weeks.toFloat() * 7 * 24 * 60 * 60;
            return value;
        }

        // Months check
        matches = value.match(/^([0-9\.]+) month[s]?$/i);
        if (matches !== null) {
            var months = matches.pop();
            value = months.toFloat() * 30 * 24 * 60 * 60;
            return value;
        }

        // Nothing to normalize
        return value;
    };

    /**
     * __operate
     * 
     * @throws  Error
     * @access  private
     * @param   mixed value
     * @param   String operator
     * @param   mixed comparison
     * @return  Boolean
     */
    var __operate = function(value, operator, comparison) {
        comparison = __normalizeComparisonValue(comparison);
        if (operator === '===') {
            var valid = value === comparison;
            return valid;
        }
        if (operator === '>') {
            var valid = value > comparison;
            return valid;
        }
        if (operator === '>=') {
            var valid = value >= comparison;
            return valid;
        }
        if (operator === '<') {
            var valid = value < comparison;
            return valid;
        }
        if (operator === '<=') {
            var valid = value <= comparison;
            return valid;
        }
        if (operator === 'IN') {
            var valid = comparison.indexOf(value) !== -1;
            return valid;
        }
        var msg = 'Invalid operator';
        throw new Error(msg);
    };

